<# 
Ext4WinInstaller.ps1 - v4.0 - by Pawel Zorzan Urban 'okno' - zorzan.pawel@gmail.com (single-file installer/uninstaller)
Self-extracting payload embedded as Base64 ZIP.

Usage (Admin):
  powershell -NoProfile -ExecutionPolicy Bypass -File .\Ext4WinInstaller.ps1
  powershell -NoProfile -ExecutionPolicy Bypass -File .\Ext4WinInstaller.ps1 -InstallDir C:\ext4win -Distro Debian -Language auto
  powershell -NoProfile -ExecutionPolicy Bypass -File .\Ext4WinInstaller.ps1 -Uninstall

Notes:
- Mount/Unmount requires Admin.
- Tray/Agent are installed as Scheduled Tasks (elevated, interactive).
#>

[CmdletBinding()]
param(
  [string]$InstallDir = 'C:\ext4win',
  [string]$Distro = 'Debian',
  [ValidateSet('auto','it','en')][string]$Language = 'auto',
  [switch]$Uninstall
)

Set-StrictMode -Off
$ErrorActionPreference = 'Stop'

$Product = 'Ext4Win'
$Version = '4.0'
$Publisher = 'Ext4Win'

function Test-Admin {
  try {
    $id = [Security.Principal.WindowsIdentity]::GetCurrent()
    $p  = New-Object Security.Principal.WindowsPrincipal $id
    return $p.IsInRole([Security.Principal.WindowsBuiltinRole]::Administrator)
  } catch { return $false }
}

function Relaunch-Admin {
  param([string[]]$ExtraArgs)
  $ps = Join-Path $env:SystemRoot 'System32\WindowsPowerShell\v1.0\powershell.exe'
  $self = $PSCommandPath
  $args = @('-NoProfile','-ExecutionPolicy','Bypass','-File',"`"$self`"") + $ExtraArgs
  Start-Process -FilePath $ps -ArgumentList $args -Verb RunAs | Out-Null
}

function Ensure-Dir([string]$Path) {
  if (-not (Test-Path -LiteralPath $Path)) { New-Item -ItemType Directory -Force -Path $Path | Out-Null }
}

function Write-Info($m){ Write-Host "[Ext4Win] $m" }
function Write-Warn($m){ Write-Warning "[Ext4Win] $m" }

# ---------------- payload (Base64 ZIP) ----------------
$PayloadB64 = @'

'@

function Extract-PayloadToTemp {
  $tmp = Join-Path $env:TEMP ("ext4win_payload_{0}" -f ([Guid]::NewGuid().ToString('N')))
  Ensure-Dir $tmp
  $zipPath = Join-Path $tmp 'payload.zip'

  $bytes = [Convert]::FromBase64String(($PayloadB64 -replace '\s+',''))
  [System.IO.File]::WriteAllBytes($zipPath, $bytes)

  Expand-Archive -LiteralPath $zipPath -DestinationPath $tmp -Force
  Remove-Item -LiteralPath $zipPath -Force -ErrorAction SilentlyContinue
  return $tmp
}

# ---------------- Scheduled Tasks (XML) ----------------
function Get-TaskXml {
  param(
    [Parameter(Mandatory=$true)][string]$TaskName,
    [Parameter(Mandatory=$true)][string]$Command,
    [Parameter(Mandatory=$true)][string]$Arguments,
    [Parameter(Mandatory=$true)][string]$WorkingDirectory,
    [switch]$OnLogonTrigger,
    [switch]$StartOnDemandOnly
  )

  $author = $Publisher
  $userId = "{0}\{1}" -f $env:USERDOMAIN, $env:USERNAME

  $triggerXml = ''
  if ($OnLogonTrigger) {
    $triggerXml = @"
    <LogonTrigger>
      <Enabled>true</Enabled>
    </LogonTrigger>
"@
  } else {
    # Disabled far-future trigger (task can still be started on-demand)
    $triggerXml = @"
    <TimeTrigger>
      <StartBoundary>2099-01-01T00:00:00</StartBoundary>
      <Enabled>false</Enabled>
    </TimeTrigger>
"@
  }

# NOTE: We set LogonType=InteractiveToken to avoid password prompts.
    $CommandEsc = [System.Security.SecurityElement]::Escape($Command)
  $ArgumentsEsc = [System.Security.SecurityElement]::Escape($Arguments)
  $WorkingDirectoryEsc = [System.Security.SecurityElement]::Escape($WorkingDirectory)

$xml = @"
<?xml version="1.0" encoding="UTF-16"?>
<Task version="1.4" xmlns="http://schemas.microsoft.com/windows/2004/02/mit/task">
  <RegistrationInfo>
    <Date>$(Get-Date -Format s)</Date>
    <Author>$author</Author>
    <Description>$Product - $TaskName</Description>
  </RegistrationInfo>
  <Principals>
    <Principal id="Author">
      <UserId>$userId</UserId>
      <LogonType>InteractiveToken</LogonType>
      <RunLevel>HighestAvailable</RunLevel>
    </Principal>
  </Principals>
  <Settings>
    <MultipleInstancesPolicy>IgnoreNew</MultipleInstancesPolicy>
    <DisallowStartIfOnBatteries>false</DisallowStartIfOnBatteries>
    <StopIfGoingOnBatteries>false</StopIfGoingOnBatteries>
    <AllowHardTerminate>true</AllowHardTerminate>
    <StartWhenAvailable>true</StartWhenAvailable>
    <RunOnlyIfNetworkAvailable>false</RunOnlyIfNetworkAvailable>
    <IdleSettings>
      <StopOnIdleEnd>false</StopOnIdleEnd>
      <RestartOnIdle>false</RestartOnIdle>
    </IdleSettings>
    <AllowStartOnDemand>true</AllowStartOnDemand>
    <Enabled>true</Enabled>
    <Hidden>false</Hidden>
    <RunOnlyIfIdle>false</RunOnlyIfIdle>
    <WakeToRun>false</WakeToRun>
    <ExecutionTimeLimit>PT0S</ExecutionTimeLimit>
    <Priority>7</Priority>
  </Settings>
  <Triggers>
$triggerXml
  </Triggers>
  <Actions Context="Author">
    <Exec>
      <Command>$CommandEsc</Command>
      <Arguments>$ArgumentsEsc</Arguments>
      <WorkingDirectory>$WorkingDirectoryEsc</WorkingDirectory>
    </Exec>
  </Actions>
</Task>
"@
  return $xml
}

function Install-TaskXml {
  param(
    [Parameter(Mandatory=$true)][string]$Name,
    [Parameter(Mandatory=$true)][string]$XmlContent
  )
  $schtasks = Join-Path $env:SystemRoot 'System32\schtasks.exe'
  if (-not (Test-Path -LiteralPath $schtasks)) { $schtasks = 'schtasks.exe' }

  $tmp = Join-Path $env:TEMP ("{0}_{1}.xml" -f $Name, ([Guid]::NewGuid().ToString('N')))
  [System.IO.File]::WriteAllText($tmp, $XmlContent, [System.Text.Encoding]::Unicode)

  & $schtasks /Create /TN $Name /XML $tmp /F | Out-Null
  Remove-Item -LiteralPath $tmp -Force -ErrorAction SilentlyContinue
}

function Remove-TaskSafe([string]$Name) {
  $schtasks = Join-Path $env:SystemRoot 'System32\schtasks.exe'
  if (-not (Test-Path -LiteralPath $schtasks)) { $schtasks = 'schtasks.exe' }
  try { & $schtasks /End /TN $Name 2>$null | Out-Null } catch { }
  try { & $schtasks /Delete /TN $Name /F 2>$null | Out-Null } catch { }
}

function Run-TaskSafe([string]$Name) {
  $schtasks = Join-Path $env:SystemRoot 'System32\schtasks.exe'
  if (-not (Test-Path -LiteralPath $schtasks)) { $schtasks = 'schtasks.exe' }
  try { & $schtasks /Run /TN $Name 2>$null | Out-Null } catch { }
}

# ---------------- Shortcuts ----------------
function Create-Shortcut {
  param(
    [Parameter(Mandatory=$true)][string]$ShortcutPath,
    [Parameter(Mandatory=$true)][string]$TargetPath,
    [string]$Arguments = '',
    [string]$WorkingDirectory = '',
    [string]$IconLocation = ''
  )
  $shell = New-Object -ComObject WScript.Shell
  $sc = $shell.CreateShortcut($ShortcutPath)
  $sc.TargetPath = $TargetPath
  if ($Arguments) { $sc.Arguments = $Arguments }
  if ($WorkingDirectory) { $sc.WorkingDirectory = $WorkingDirectory }
  if ($IconLocation) { $sc.IconLocation = $IconLocation }
  $sc.Save()
}

function Remove-FileSafe([string]$Path) { try { Remove-Item -LiteralPath $Path -Force -ErrorAction SilentlyContinue } catch { } }

# ---------------- Install/Uninstall ----------------
$TaskTray    = 'Ext4Win_Tray'
$TaskAgent   = 'Ext4Win_Agent'
$TaskMount   = 'Ext4Win_MountAll'
$TaskUnmount = 'Ext4Win_UnmountAll'

if (-not (Test-Admin)) {
  $extra = @()
  if ($Uninstall) { $extra += '-Uninstall' }
  if ($InstallDir) { $extra += @('-InstallDir', $InstallDir) }
  if ($Distro) { $extra += @('-Distro', $Distro) }
  if ($Language) { $extra += @('-Language', $Language) }
  Relaunch-Admin -ExtraArgs $extra
  exit 0
}

if ($Uninstall) {
  Write-Info "Disinstallazione da $InstallDir ..."

  # Best-effort unmount all (if present)
  try {
    $ctl = Join-Path $InstallDir 'Ext4WinCtl.ps1'
    if (Test-Path -LiteralPath $ctl) {
      & (Join-Path $env:SystemRoot 'System32\WindowsPowerShell\v1.0\powershell.exe') -NoProfile -ExecutionPolicy Bypass -File $ctl -Action UnmountAll 2>$null | Out-Null
    }
  } catch { }

  # Stop/remove tasks
  Remove-TaskSafe $TaskAgent
  Remove-TaskSafe $TaskTray
  Remove-TaskSafe $TaskMount
  Remove-TaskSafe $TaskUnmount

  # Shortcuts
  $desktop = [Environment]::GetFolderPath('Desktop')
  Remove-FileSafe (Join-Path $desktop 'MONTA.lnk')
  Remove-FileSafe (Join-Path $desktop 'SMONTA.lnk')

  $sm = Join-Path $env:APPDATA 'Microsoft\Windows\Start Menu\Programs\Ext4Win'
  try { Remove-Item -LiteralPath $sm -Recurse -Force -ErrorAction SilentlyContinue } catch { }

  # Uninstall registry entry
  try {
    $k = 'HKLM:\Software\Microsoft\Windows\CurrentVersion\Uninstall\Ext4Win'
    if (Test-Path $k) { Remove-Item -Path $k -Recurse -Force -ErrorAction SilentlyContinue }
  } catch { }

  # Remove files
  try { Remove-Item -LiteralPath $InstallDir -Recurse -Force -ErrorAction SilentlyContinue } catch { }

  Write-Info "Disinstallazione completata."
  exit 0
}

Write-Info "Installazione Ext4Win v$Version in $InstallDir ..."

Ensure-Dir $InstallDir
Ensure-Dir (Join-Path $InstallDir 'logs')
Ensure-Dir (Join-Path $InstallDir 'run')
Ensure-Dir (Join-Path $InstallDir 'docs')
Ensure-Dir (Join-Path $InstallDir 'src')

$tmp = Extract-PayloadToTemp
try {
  # Copy payload, preserve user icon if already present
  Get-ChildItem -LiteralPath $tmp -File -Recurse | ForEach-Object {
    $rel = $_.FullName.Substring($tmp.Length).TrimStart('\','/')
    if (-not $rel) { return }
    $dest = Join-Path $InstallDir $rel

    $destDir = Split-Path -Parent $dest
    Ensure-Dir $destDir

    if ($rel -ieq 'file.ico' -and (Test-Path -LiteralPath (Join-Path $InstallDir 'file.ico'))) {
      return
    }

    Copy-Item -LiteralPath $_.FullName -Destination $dest -Force
  }
} finally {
  try { Remove-Item -LiteralPath $tmp -Recurse -Force -ErrorAction SilentlyContinue } catch { }
}

# Update/merge config
$configPath = Join-Path $InstallDir 'config.json'
$cfg = $null
try { if (Test-Path -LiteralPath $configPath) { $cfg = (Get-Content -LiteralPath $configPath -Raw) | ConvertFrom-Json } } catch { $cfg = $null }
if (-not $cfg) { $cfg = [pscustomobject]@{} }

try { $cfg | Add-Member -Force -NotePropertyName 'install_dir' -NotePropertyValue $InstallDir } catch { }
try { $cfg | Add-Member -Force -NotePropertyName 'distro' -NotePropertyValue $Distro } catch { }
try { $cfg | Add-Member -Force -NotePropertyName 'language' -NotePropertyValue $Language } catch { }

($cfg | ConvertTo-Json -Depth 10) | Set-Content -LiteralPath $configPath -Encoding UTF8

# Copy installer itself for uninstall later
try { Copy-Item -LiteralPath $PSCommandPath -Destination (Join-Path $InstallDir 'Ext4WinInstaller.ps1') -Force } catch { }

# Create Scheduled Tasks
$ps = Join-Path $env:SystemRoot 'System32\WindowsPowerShell\v1.0\powershell.exe'

$trayOut = Join-Path $InstallDir 'logs\Ext4WinTray.out.log'
$agentOut = Join-Path $InstallDir 'logs\Ext4WinAgent.out.log'

$trayArgs = '/c ""{0}" -NoProfile -ExecutionPolicy Bypass -STA -WindowStyle Hidden -File "{1}" 1>>"{2}" 2>>&1"' -f $ps, (Join-Path $InstallDir 'Ext4WinTray.ps1'), $trayOut
$agentArgs = '/c ""{0}" -NoProfile -ExecutionPolicy Bypass -WindowStyle Hidden -File "{1}" 1>>"{2}" 2>>&1"' -f $ps, (Join-Path $InstallDir 'Ext4WinAgent.ps1'), $agentOut

$mountArgs = '/c ""{0}" -NoProfile -ExecutionPolicy Bypass -WindowStyle Hidden -File "{1}" -Action MountAll"' -f $ps, (Join-Path $InstallDir 'Ext4WinCtl.ps1')
$unmountArgs = '/c ""{0}" -NoProfile -ExecutionPolicy Bypass -WindowStyle Hidden -File "{1}" -Action UnmountAll"' -f $ps, (Join-Path $InstallDir 'Ext4WinCtl.ps1')

$xmlTray = Get-TaskXml -TaskName $TaskTray -Command 'cmd.exe' -Arguments $trayArgs -WorkingDirectory $InstallDir -OnLogonTrigger
$xmlAgent = Get-TaskXml -TaskName $TaskAgent -Command 'cmd.exe' -Arguments $agentArgs -WorkingDirectory $InstallDir -OnLogonTrigger
$xmlMount = Get-TaskXml -TaskName $TaskMount -Command 'cmd.exe' -Arguments $mountArgs -WorkingDirectory $InstallDir
$xmlUnmount = Get-TaskXml -TaskName $TaskUnmount -Command 'cmd.exe' -Arguments $unmountArgs -WorkingDirectory $InstallDir

Install-TaskXml -Name $TaskTray -XmlContent $xmlTray
Install-TaskXml -Name $TaskAgent -XmlContent $xmlAgent
Install-TaskXml -Name $TaskMount -XmlContent $xmlMount
Install-TaskXml -Name $TaskUnmount -XmlContent $xmlUnmount

# Desktop shortcuts (MONTA / SMONTA)
$desktop = [Environment]::GetFolderPath('Desktop')
$ico = Join-Path $InstallDir 'file.ico'
Create-Shortcut -ShortcutPath (Join-Path $desktop 'MONTA.lnk') -TargetPath 'schtasks.exe' -Arguments ("/Run /TN {0}" -f $TaskMount) -WorkingDirectory $InstallDir -IconLocation $ico
Create-Shortcut -ShortcutPath (Join-Path $desktop 'SMONTA.lnk') -TargetPath 'schtasks.exe' -Arguments ("/Run /TN {0}" -f $TaskUnmount) -WorkingDirectory $InstallDir -IconLocation $ico

# Start Menu shortcuts (current user)
$sm = Join-Path $env:APPDATA 'Microsoft\Windows\Start Menu\Programs\Ext4Win'
Ensure-Dir $sm
$readmeRel = 'docs\README.md'
if ($Language -eq 'en') { $readmeRel = 'docs\README.en.md' }
$readmePath = Join-Path $InstallDir $readmeRel

Create-Shortcut -ShortcutPath (Join-Path $sm 'Ext4Win - Docs.lnk') -TargetPath 'notepad.exe' -Arguments ("`"{0}`"" -f $readmePath) -WorkingDirectory $InstallDir -IconLocation $ico
Create-Shortcut -ShortcutPath (Join-Path $sm 'Ext4Win - Logs.lnk') -TargetPath 'explorer.exe' -Arguments ("`"{0}`"" -f (Join-Path $InstallDir 'logs')) -WorkingDirectory $InstallDir -IconLocation $ico
Create-Shortcut -ShortcutPath (Join-Path $sm 'Ext4Win - Uninstall.lnk') -TargetPath $ps -Arguments ("-NoProfile -ExecutionPolicy Bypass -File `"{0}`" -Uninstall" -f (Join-Path $InstallDir 'Ext4WinInstaller.ps1')) -WorkingDirectory $InstallDir -IconLocation $ico

# Add/Remove Programs entry (best-effort)
try {
  $k = 'HKLM:\Software\Microsoft\Windows\CurrentVersion\Uninstall\Ext4Win'
  New-Item -Path $k -Force | Out-Null
  Set-ItemProperty -Path $k -Name DisplayName -Value $Product
  Set-ItemProperty -Path $k -Name DisplayVersion -Value $Version
  Set-ItemProperty -Path $k -Name Publisher -Value $Publisher
  Set-ItemProperty -Path $k -Name InstallLocation -Value $InstallDir
  $u = ("{0} -NoProfile -ExecutionPolicy Bypass -File `"{1}`" -Uninstall" -f $ps, (Join-Path $InstallDir 'Ext4WinInstaller.ps1'))
  Set-ItemProperty -Path $k -Name UninstallString -Value $u
} catch { }

# Start tasks now
Run-TaskSafe $TaskTray
Run-TaskSafe $TaskAgent

Write-Info "Installazione completata."
Write-Host ""
Write-Host "Test consigliato (Admin):"
Write-Host ("  {0}\Ext4WinCtl.ps1 -Action Prereqs" -f $InstallDir)
Write-Host ("  {0}\Ext4WinCtl.ps1 -Action ListExt4" -f $InstallDir)
Write-Host ("  {0}\Ext4WinCtl.ps1 -Action MountAll" -f $InstallDir)
Write-Host ("  {0}\Ext4WinCtl.ps1 -Action ListMounts" -f $InstallDir)
